let unhealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'Unhealthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId;
let healthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'Healthy' 
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId;
let notApplicable = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'NotApplicable' 
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId; 
let notUnhealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'Unhealthy' 
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId; 
let notHealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'Healthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId; 
let notNotApplicable = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'NotApplicable' 
| where isnotempty(RecommendationId) and isnotempty(RecommendationName) 
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId; 
let notHealthyToHealthy =  
    notHealthy 
    | join (
        healthy
      ) on RecommendationId, AssessedResourceId 
    | where TimeGenerated < TimeGenerated1 
    | summarize ToHealthyCount = count(), RecommendationName=any(RecommendationName) by RecommendationId;
let notUnhealthyToUnhealthy =  
    notUnhealthy 
    | join ( 
        unhealthy
     ) on RecommendationId, AssessedResourceId 
    | where TimeGenerated < TimeGenerated1 
    | summarize ToUnhealthyCount = count(), RecommendationName=any(RecommendationName) by RecommendationId;
let notNotApplicableToNotApplicable =  
    notNotApplicable 
    | join (
        notApplicable
     ) on RecommendationId, AssessedResourceId 
    | where TimeGenerated < TimeGenerated1 
    | summarize ToNotApplicableCount = count(), RecommendationName=any(RecommendationName) by RecommendationId;
// Union 
union notHealthyToHealthy, notUnhealthyToUnhealthy, notNotApplicableToNotApplicable
| summarize RecommendationName=any(RecommendationName), ToUnhealthyCount = sum(ToUnhealthyCount), ToHealthyCount = sum(ToHealthyCount), ToNotApplicableCount = sum(ToNotApplicableCount) by RecommendationId
| order by ToUnhealthyCount desc","size":0,"noDataMessage":"No data available. Check your continuous export configuration for the selected workspaces.","timeContextFromParameter":"TimeRange","exportedParameters":[{"fieldName":"RecommendationId","parameterName":"RecommendationId"},{"fieldName":"RecommendationName","parameterName":"RecommendationName","parameterType":1}],"queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{workspaceName}"],"gridSettings":{"formatters":[{"columnMatch":"RecommendationId","formatter":5},{"columnMatch":"RecommendationName","formatter":7,"formatOptions":{"linkTarget":"Url","bladeOpenContext":{"bladeName":"RecommendationsBlade","extensionName":"Microsoft_Azure_Security","bladeParameters":[{"name":"assessmentKey","source":"column","value":"RecommendationId"}]},"customColumnWidthSetting":"100ch"}},{"columnMatch":"UnhealthyCount","formatter":8,"formatOptions":{"palette":"redBright"}},{"columnMatch":"HealthyCount","formatter":8,"formatOptions":{"palette":"green"}},{"columnMatch":"NotApplicableCount","formatter":8,"formatOptions":{"palette":"gray"}},{"columnMatch":"AssessedResourceId","formatter":13,"formatOptions":{"linkTarget":null,"showIcon":true}}],"labelSettings":[{"columnId":"RecommendationName","label":"Recommendation name"},{"columnId":"ToUnhealthyCount","label":"To unhealthy"},{"columnId":"ToHealthyCount","label":"To healthy"},{"columnId":"ToNotApplicableCount","label":"To not applicable"}]},"sortBy":[]},"name":"RecommendationStatusChanges"},{"type":1,"content":{"json":"To view changes over time on a specific recommendation, please select any from the list above.","style":"info"},"conditionalVisibility":{"parameterName":"RecommendationId","comparison":"isEqualTo"},"name":"ChangeLogBanner"},{"type":3,"content":{"version":"KqlItem/1.0","query":"let unhealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'Unhealthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId
| project UnhealthyRecommendationId = RecommendationId, UnhealthyResourceId = AssessedResourceId, UnhealhyTime =  TimeGenerated, tostring(SubscriptionId);
let notApplicable = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'NotApplicable'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId
| project NARecommendationId = RecommendationId, NAResourceId = AssessedResourceId, NATime =  TimeGenerated, tostring(SubscriptionId);
let healthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState == 'Healthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId
| project HealthyRecommendationId = RecommendationId, HealthyResourceId = AssessedResourceId, HealhyTime =  TimeGenerated, tostring(SubscriptionId);
let NotHealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'Healthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId;
let NotUnhealthy = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'Unhealthy'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId;
let NotNotApplicable = SecurityRecommendation
| extend SubscriptionId = iff(AssessedResourceId=="N/A", split(url_decode(RecommendationLink),'/')[9], split(AssessedResourceId, '/')[2])
| where '{selectedSubscription}' == 'All' or SubscriptionId == '{selectedSubscription}'
| where RecommendationState !~ 'NotApplicable'
| where isnotempty(RecommendationId) and isnotempty(RecommendationName)
| summarize arg_max(TimeGenerated, *) by RecommendationId, AssessedResourceId;
let 1_to_Healthy = 
    NotHealthy
    | extend orignalState = RecommendationState
    | join healthy on $left.RecommendationId == $right.HealthyRecommendationId, $left.AssessedResourceId == $right.HealthyResourceId
    | where TimeGenerated < HealhyTime
    | extend update = "To healthy"
    | project RecommendationId, RecommendationName, Description, OriginalState = RecommendationState, update, TimeGenerated, RecommendationSeverity, tostring(SubscriptionId), AssessedResourceId, RecommendationLink;
//1_to_Healthy
let 2_to_Unhealthy = 
    NotUnhealthy
    | extend orignalState = RecommendationState
    | join unhealthy on $left.RecommendationId == $right.UnhealthyRecommendationId, $left.AssessedResourceId == $right.UnhealthyResourceId
    | where TimeGenerated < UnhealhyTime
    | extend update = "To unhealthy"
    | project RecommendationId, RecommendationName, Description, OriginalState = RecommendationState, update, TimeGenerated, RecommendationSeverity, tostring(SubscriptionId), AssessedResourceId, RecommendationLink;
//2_to_Unhealthy
let 3_to_NotApplicable = 
    NotNotApplicable
    | extend orignalState = RecommendationState
    | join notApplicable on $left.RecommendationId == $right.NARecommendationId, $left.AssessedResourceId == $right.NAResourceId
    | where TimeGenerated < NATime
    | extend update = "To not applicable"
    | extend NotApplicableReason = iff(isempty(NotApplicableReason), "NA", NotApplicableReason)
    | project RecommendationId, RecommendationName, Description, OriginalState = RecommendationState, update, TimeGenerated, RecommendationSeverity, tostring(SubscriptionId), AssessedResourceId, RecommendationLink, NotApplicableReason;
// JOIN
union 1_to_Healthy, 2_to_Unhealthy, 3_to_NotApplicable
| extend FullRecommendationLink = strcat("http://",RecommendationLink)
| extend AssessedResourceId = iff(AssessedResourceId=="N/A", extract(".*onPremiseMachines/(.+)",1, url_decode(RecommendationLink)), AssessedResourceId)
| project-away RecommendationLink
| where RecommendationId == '{RecommendationId}'","size":0,"title":"Changes for "{RecommendationName}"","noDataMessage":"No data available. Check your continuous export configuration for the selected workspaces.","showExportToExcel":true,"exportToExcelOptions":"all","queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{workspaceName}"],"gridSettings":{"formatters":[{"columnMatch":"RecommendationId","formatter":5},{"columnMatch":"RecommendationName","formatter":5},{"columnMatch":"Description","formatter":5},{"columnMatch":"SubscriptionId","formatter":15,"formatOptions":{"linkTarget":null,"showIcon":true}},{"columnMatch":"NotApplicableReason","formatter":0,"formatOptions":{"customColumnWidthSetting":"30ch"}},{"columnMatch":"FullRecommendationLink","formatter":7,"formatOptions":{"linkTarget":"Url","linkLabel":"View","linkIsContextBlade":false}}],"rowLimit":1000,"hierarchySettings":{"treeType":1,"groupBy":["update"]},"labelSettings":[{"columnId":"RecommendationName","label":"Recommendation name"},{"columnId":"OriginalState","label":"Original state"},{"columnId":"update","label":"Updated state"},{"columnId":"TimeGenerated","label":"Time of change"},{"columnId":"RecommendationSeverity","label":"Severity"},{"columnId":"SubscriptionId","label":"Subscription"},{"columnId":"AssessedResourceId","label":"Resource"},{"columnId":"NotApplicableReason","label":"Reason"},{"columnId":"FullRecommendationLink","label":"View recommendation"}]},"sortBy":[]},"conditionalVisibility":{"parameterName":"RecommendationId","comparison":"isNotEqualTo"},"name":"ChangeLogDetails"}]},"conditionalVisibilities":[{"parameterName":"workspaceName","comparison":"isNotEqualTo"},{"parameterName":"ErrorHandle","comparison":"isNotEqualTo","value":"1"}],"name":"ChangeLog"},{"type":12,"content":{"version":"NotebookGroup/1.0","groupType":"editable","title":"Machines not sending current heartbeats","items":[{"type":3,"content":{"version":"KqlItem/1.0","query":"Heartbeat
