let endDateTime = {timeRange:end};
let startDateTime = {timeRange:start};
let trendBinSize = {timeRange:grain};
let controllerName= '{workloadName}';
KubePodInventory
| where TimeGenerated >= startDateTime
| where TimeGenerated < endDateTime
{clusterIdWhereClause}
{workloadKindWhereClause}
{namespaceWhereClause}
| where isnotempty(ClusterName)
| where isnotempty(Namespace)
| where ControllerName == controllerName
| extend PodName = Name
{podStatusWhereClause}
{podTileStatusWhereClause}
{podNameWhereClause}
| summarize PodRestartCount=max(PodRestartCount) by PodName, bin(TimeGenerated, trendBinSize)
| order by PodName asc nulls last, TimeGenerated asc
| serialize 
| extend prevValue=iif(prev(PodName) == PodName, prev(PodRestartCount), PodRestartCount)
| extend RestartCount=PodRestartCount - prevValue
| extend RestartCount=iif(RestartCount < 0, 0, RestartCount) 
| project TimeGenerated, PodName, RestartCount
| render timechart

let endDateTime = {timeRange:end};
let startDateTime = {timeRange:start};
let trendBinSize = {timeRange:grain};
let controllerName= '{workloadName:value}';
KubePodInventory
| where TimeGenerated >= startDateTime
| where TimeGenerated < endDateTime
{clusterIdWhereClause}
{workloadKindWhereClause}
{namespaceWhereClause}
| where isnotempty(ClusterName)
| where isnotempty(Namespace)
| where ControllerName == controllerName
| extend PodName = Name
{podStatusWhereClause}
{podTileStatusWhereClause}
{podNameWhereClause}
| extend ContainerName=tostring(split(ContainerName, '/')[1])
| where isempty(ContainerName) == false
| summarize ContainerRestartCount=sum(ContainerRestartCount) by ContainerName, bin(TimeGenerated, 1tick)
| order by ContainerName asc nulls last, TimeGenerated asc
| serialize 
| extend prevValue=iif(prev(ContainerName) == ContainerName, prev(ContainerRestartCount), ContainerRestartCount)
| extend RestartCount=ContainerRestartCount - prevValue
| extend RestartCount=iif(RestartCount < 0, 0, RestartCount) 
| project TimeGenerated, ContainerName, RestartCount
| summarize RestartCount=sum(RestartCount) by ContainerName, bin(TimeGenerated, trendBinSize)

