//Current score trends per subscription (show current, 7-day change from the current score as %, 30-day change from the current score as %)
let startOfToday = startofday(now()); 
let offsetWeek = 6d; 
let offsetMonth = 29d; 
let lookbackDurationWeek = 14d; 
let lookbackDurationMonth = 45d; 
let endTimeWeek = startOfToday - offsetWeek; 
let startTimeWeek = endTimeWeek - lookbackDurationWeek; 
let endTimeMonth = startOfToday - offsetMonth; 
let startTimeMonth = endTimeMonth - lookbackDurationMonth; 
SecureScores 
| extend Day = startofday(TimeGenerated) 
| summarize arg_max(TimeGenerated, *) by Day, SecureScoresSubscriptionId 
| summarize arg_max(Day, *) by SecureScoresSubscriptionId 
| join kind = fullouter( 
    SecureScores 
    | extend Day = startofday(TimeGenerated) 
    | where TimeGenerated > startTimeWeek and TimeGenerated <= endTimeWeek 
    | summarize arg_max(TimeGenerated, *) by SecureScoresSubscriptionId 
    | project OldScoreSevenDays = PercentageScore, SecureScoresSubscriptionId 
    ) 
    on SecureScoresSubscriptionId 
| join kind = fullouter( 
    SecureScores 
    | extend Day = startofday(TimeGenerated) 
    | where TimeGenerated > startTimeMonth and TimeGenerated <= endTimeMonth 
    | summarize arg_max(TimeGenerated, *) by SecureScoresSubscriptionId 
    | project OldMonthScore = PercentageScore, SecureScoresSubscriptionId 
    ) 
    on SecureScoresSubscriptionId 
| extend DiffSevenDays = tostring(((PercentageScore - OldScoreSevenDays) / OldScoreSevenDays) * 100) 
| extend DiffSevenDays = iff(isempty(DiffSevenDays), "", DiffSevenDays) 
| extend DiffMonth = tostring(((PercentageScore - OldMonthScore) / OldMonthScore) * 100) 
| extend DiffMonth = iff(isempty(DiffMonth), "", DiffMonth) 
| project SecureScoresSubscriptionId, CurrentScore = PercentageScore * 100, todouble(DiffSevenDays), todouble(DiffMonth)
